mapper.select:
	    select *
	    from user
	    where 1=1


        and username = #{username}



        and phone = #{phone}

select teacher_info.userid,teacher_info.realname,teacher_course.courseId,teacher_course.coursePrice,course.courseName,course.grade
from teacher_info
LEFT JOIN teacher_course on teacher_info.userid=teacher_course.teacherId
LEFT JOIN course on teacher_course.courseId=course.courseId
where 1=1


	and teacher_info.userid = #{userId}



	and teacher_course.courseId = #{courseId}



	and course.grade = #{grade}



	and teacher_info.realname = #{realName}



	and course.courseName = #{courseName}
